import java.io.*;import java.util.*;import java.util.HashSet;import java.util.Scanner;import java.util.Set;import java.util.ArrayList;import java.util.Collections;import java.util.HashMap;import java.util.Map;import java.text.DecimalFormat;import java.lang.Math;import java.util.Iterator; public class b90{	public static void main(String[] args){		Scanner sc = new Scanner(System.in);		int x1 = sc.nextInt();		int y1 = sc.nextInt();		int r1 = sc.nextInt();		int x2 = sc.nextInt();		int y2 = sc.nextInt();		int r2 = sc.nextInt();		double d = Math.sqrt(Math.pow(x1-x2, 2)+Math.pow(y1-y2, 2));		if(Math.pow(x1, 2)+Math.pow(y1, 2)==Math.pow(r1, 2) && Math.pow(x2, 2)+Math.pow(y2, 2)==Math.pow(r2, 2)){		    System.out.println(0);		}		else if(d <= (double)(r1+ r2) && 2*d >= (double)(r1+ r2)){		    System.out.println(0);		}		else if(d < (double)Math.max(r1, r2)){		    System.out.println(((double)Math.max(r1, r2)-(d+(double)Math.min(r1, r2)))/2);		}		else{		    System.out.println((d-(double)(r1+r2))/2);		}	}	}
