import java.util.HashSet;import java.util.*; import java.util.Scanner;import java.util.Set;import java.util.ArrayList;import java.util.Collections;import java.util.HashMap;import java.util.Map;import java.text.DecimalFormat;import java.lang.Math;public class Main{    public static int gcd(int a, int b){        if(b==0){            return a;        }        else{            return gcd(b, a%b);        }    }	public static void main(String[] args){		Scanner sc = new Scanner(System.in);		int p1 = sc.nextInt();		int p2 = sc.nextInt();		int[] a = new int[p1+1];		int[] b = new int[p2+1];		for(int i = 0; i <= p1; i++){		    a[i] = sc.nextInt();		}		for(int i = 0; i <= p2; i++){		    b[i] = sc.nextInt();		}		if(p1<p2){		    System.out.println("0/1");		}		else if(p1==p2){		    if((float)a[0]/b[0] > 0){		        System.out.println((a[0]/gcd(a[0], b[0])) +"/"+(b[0]/gcd(a[0], b[0])));		    }		    else{		        if(a[0]>0){		            System.out.println(-(a[0]/gcd(a[0], Math.abs(b[0])))+"/"+(Math.abs(b[0])/gcd(a[0], Math.abs(b[0]))));		        }		        else{		            System.out.println((a[0]/gcd(Math.abs(a[0]), b[0]))+"/"+(Math.abs(b[0])/gcd(Math.abs(a[0]), Math.abs(b[0]))));		        }		    }		}		else{		    if(((float)a[0]/b[0]) > 0){		        System.out.println("Infinity");		    }		    else{		        System.out.println("-Infinity");		    }		}	}}
