//package CodeForces; import java.util.Scanner; public class AboutBacteria { 	public static void main(String[] args) {		// TODO Auto-generated method stub		Scanner s=new Scanner(System.in);		double k=s.nextInt();double b=s.nextInt();double n=s.nextInt();double t=s.nextInt();		if(k==1)		{			if(t>=1+n*b)			{				System.out.println(0);			}			else			{				double ans=(((1+n*b-t)*(1.0))/((b)*1.0));				for(int i=0;;)				{					if(i>=ans)					{						System.out.println(i);						break;					}					else					{						i++;					}				}			}		}		else		{			double ans=(((t*(k-1)+b)*(1.0))/((k-1+b)*(1.0)));			for(int i=0;;)			{				if(Math.pow(k,n-i)>ans)				{					i++;				}				else				{					System.out.println(i);					break;				}			}		} 	} }
