#119Adef main():    from sys import stdin, stdout    a=[0,1,1]    for _ in range(43):        a.append(a[-1]+a[-2])    #print(a)    #print(len(a))    #print(a[-1])     n = int(stdin.readline())    if n==0:        print(0,0,0)        return    fp = 0    sp = 45    while fp<sp:        if a[fp]+a[sp] == n:            break        elif a[fp]+a[sp] < n:            fp+=1        elif a[fp]+a[sp] > n:            sp-=1     if a[fp]+a[sp]==n:        stdout.write(str(a[fp])+' '+str(a[sp-2])+' '+str(a[sp-1])+'\n')    else:        stdout.write("I'm too stupid to solve this problem\n") if __name__=='__main__':    main()
