import java.io.*;import java.util.*;public class Main{	 public static void main(String[] args) {	 Scanner scn=new Scanner(System.in);	 int n=scn.nextInt();		 	   int a=0;	    int b=1;	    int c=0;	    int a1=0;	    int b1=1;	    int c1=0;	    int count=0;	    while(c!=n){	     c=a+b;	      a=b;	      b=c;	      count++;	    }	    int[]arr=new int[count+2];	    arr[0]=a1;	    arr[1]=b1;	    for(int i=2;i<arr.length;i++){	        c1=a1+b1;	        a1=b1;	        b1=c1;	        arr[i]=c1;	    }	    int ans1=0;	    int ans2=0;	    if(arr.length-3>=0){	        ans1=arr[arr.length-3];	    }	    if(arr.length-2>=0){	        ans2=arr[arr.length-2];	    }	    	    System.out.println(arr[0]+" "+ans1+" "+ans2);	 	}}
