//package com.company; import java.util.*;import java.lang.*;import java.io.*; public class Main {     public static void main(String[] args)throws Exception {        Scanner sx = new Scanner(System.in);        String str = sx.next();        int[] arr = new int[26];        for(int i=0;i<str.length();i++){            arr[(int)str.charAt(i)-97]++;        }        for(int i=25;i>=0;i--){            if(arr[i]!=0){                for(int j=0;j<arr[i];j++) {                    System.out.print((char) (i + 97)+"");                }                break;            }        }    }}
