// 205A - Little Elephant and Rozdil// https://codeforces.com/problemset/problem/205/A import java.util.Scanner; public class CF205A {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        int n = sc.nextInt();        int min = Integer.MAX_VALUE;        int minIndex = 0;        int count = 1;        for (int i = 1; i <= n; i++) {            int time = sc.nextInt();            if (time < min) {                min = time;                minIndex = i;                count = 1;            } else if (time == min) {                count += 1;            }         }        System.out.println(count == 1 ? minIndex : "Still Rozdil");        sc.close();    }}
