// 208A - Dubstep// https://codeforces.com/problemset/problem/208/A import java.util.Scanner; public class CF208A {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        String s = sc.next();        boolean isDub = true;        for (int i = 0; i < s.length(); i++) {            if ((s.length() - i) > 2 && s.charAt(i) == 'W' && s.charAt(i + 1) == 'U' && s.charAt(i + 2) == 'B') {                i += 2;                if (!isDub) {                    System.out.print(" ");                }                continue;            }            isDub = false;            System.out.print(s.charAt(i));         }    }}
