//package albert.codeforces.multicoloredmarbles209a; import java.util.Scanner; public class Solution {    public static void main(String[] args) {        Scanner scan = new Scanner(System.in);        int n = scan.nextInt();        long MOD = (long) (1e9 + 7);        int[][] dp = new int[n][2];        dp[0][0] = 1;        for (int i = 1; i < n; i++) {            long current = 0;            current += dp[i - 1][i % 2];            current += dp[i - 1][(i + 1) % 2];            current++;            current %= MOD;            dp[i][(i + 1) % 2] = dp[i - 1][(i + 1) % 2];            dp[i][i % 2] = (int) current;        }        long ans = dp[n - 1][0] + dp[n - 1][1];        System.out.println(ans % MOD);     }}
