import java.util.*;public class Problem21B {    public static void main (String args[]){        Scanner sc = new Scanner (System.in);        int a1 = sc.nextInt();        int b1 = sc.nextInt();        int c1 = sc.nextInt();        int a2 = sc.nextInt();        int b2 = sc.nextInt();        int c2 = sc.nextInt();        if ((a1 == 0 && b1 == 0 && c1 != 0) || (a2 == 0 && b2 == 0 && c2 != 0)){            System.out.println(0);        }        else if ((a1 * b2 != a2 * b1)){            System.out.println(1);        }        else if ((a1 * b2 == a2 * b1) && (a1 * c2 == a2 * c1) && (b1 * c2 == b2 * c1)){            System.out.println(-1);        }        else System.out.println(0);    }}
