import java.util.*;public class square{     public static void main(String ar[])    {        Scanner sc=new Scanner(System.in);        int n=sc.nextInt(),k=sc.nextInt();    int[] height=new int[2*n+1];    for(int i=0;i<=2*n;i++)height[i]=sc.nextInt();    for(int i=1;i<=2*n;i+=2)    {        if(height[i]-Math.max(height[i-1],height[i+1])>=2){            height[i]--;k--;        }        if(k==0) break;    }    for(int x:height)System.out.print(x+" ");System.out.println();}}
