import java.util.*;import java.math.*;public class ShooshunsSequence{	public static void main(String[] args)		{			Scanner sc = new Scanner(System.in);			int n = sc.nextInt();			int k = sc.nextInt();			int[] arr = new int[n];			for(int i = 0;i<n;i++)				arr[i] = sc.nextInt();			for(int i = k-1;i<n-1;i++)			{				if(arr[i]!=arr[i+1])				{					System.out.println("-1");					return;				}			}			int temp = -2;			for(int i = 0;i<n;i++)			{				if(arr[i]!=arr[k-1])				{					temp = i;						}			}			System.out.println(temp==-2?0:temp+1);		}}
