// practice with rainboyimport java.io.*;import java.util.*; public class CF223B extends PrintWriter {	CF223B() { super(System.out, true); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF223B o = new CF223B(); o.main(); o.flush();	} 	static final int A = 26;	void main() {		byte[] ss = sc.next().getBytes();		byte[] tt = sc.next().getBytes();		int n = ss.length;		int m = tt.length;		int[] ii = new int[A];		int[] pp = new int[n];		Arrays.fill(ii, -1);		for (int i = 0; i < n; i++) {			int a = ss[i] - 'a';			pp[i] = ii[a];			ii[a] = i;		}		int[] qq = new int[n];		Arrays.fill(ii, n);		for (int i = n - 1; i >= 0; i--) {			int a = ss[i] - 'a';			qq[i] = ii[a];			ii[a] = i;		}		int[] dp = new int[n]; Arrays.fill(dp, -1);		for (int i = 0, j = 0; i < n && j < m; i++, j++) {			while (i < n && ss[i] != tt[j])				i++;			if (i < n)				dp[i] = j;		}		for (int i = 0; i < n; i++)			if (dp[i] == -1) {				int p = pp[i];				if (p != -1)					dp[i] = dp[p];			}		int[] dq = new int[n]; Arrays.fill(dq, n);		for (int i = n - 1, j = m - 1; i >= 0 && j >= 0; i--, j--) {			while (i >= 0 && ss[i] != tt[j])				i--;			if (i >= 0)				dq[i] = j;		}		for (int i = n - 1; i >= 0; i--)			if (dq[i] == n) {				int q = qq[i];				if (q != n)					dq[i] = dq[q];			}		for (int i = 0; i < n; i++)			if (dp[i] < dq[i]) {				println("No");				return;			}		println("Yes");	}}
