import java.math.*;import java.util.*;public class DiceTower{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int n = sc.nextInt();		int in = sc.nextInt();		int op = 7-in;		boolean works = false;		while(n-->0)		{			int a = sc.nextInt();			int b = sc.nextInt();			if(a == in || a == op)				works = true;			if(b == in || b == op)				works = true;		}		System.out.println(works?"NO":"YES");	}}
