// Main Code at the Bottomimport java.util.*;import java.lang.*;import java.io.*;import java.math.BigInteger; public class Main {	//Fast IO class    static class FastReader {        BufferedReader br;         StringTokenizer st;         public FastReader() {        	boolean env=System.getProperty("ONLINE_JUDGE") != null;        	if(!env) {        		try {					br=new BufferedReader(new FileReader("src\\input.txt"));				} catch (FileNotFoundException e) {					e.printStackTrace();				}        	}        	else br = new BufferedReader(new InputStreamReader(System.in));         }         String next() {            while (st == null || !st.hasMoreElements()) {                try {                    st = new StringTokenizer(br.readLine());                 }                 catch (IOException  e) {                    e.printStackTrace();                 }             }             return st.nextToken();         }         int nextInt() {            return Integer.parseInt(next());         }         long nextLong() {            return Long.parseLong(next());         }         double nextDouble() {            return Double.parseDouble(next());         }         String nextLine() {            String str = "";             try {                str = br.readLine();             }             catch (IOException e) {                e.printStackTrace();             }             return str;         }     }         static long MOD=1000000000+7;    //debug    static void debug(Object... o) {        System.out.println(Arrays.deepToString(o));    }    // Pair    static class pair{    	long x,y;    	pair(long a,long b){    		this.x=a;    		this.y=b;    	}    	public boolean equals(Object obj) {    		if(obj == null || obj.getClass()!= this.getClass()) return false;            pair p = (pair) obj;            return (this.x==p.x && this.y==p.y);        }    	public int hashCode() {            return Objects.hash(x,y);        }    }    static FastReader sc=new FastReader();    static PrintWriter out=new PrintWriter(System.out);      //Main function(The main code starts from here)    public static void main (String[] args) throws java.lang.Exception {    	int test=1;    	//test=sc.nextInt();    	while(test-->0){    		int n1=sc.nextInt(),m1=sc.nextInt();    		char a[][]=new char[n1][m1];    		for(int i=0;i<n1;i++) a[i]=sc.nextLine().toCharArray();    		int n2=sc.nextInt(),m2=sc.nextInt();    		char b[][]=new char[n2][m2];    		for(int i=0;i<n2;i++) b[i]=sc.nextLine().toCharArray();    		int max=0,X=0,Y=0;    		for(int x=-50;x<=50;x++) {    			for(int y=-50;y<=50;y++) {    				int val=0;    				for(int i=0;i<n1;i++) {    					for(int j=0;j<m1;j++) {    						if(i+x<0 || i+x>=n2 || j+y<0 || j+y>=m2) continue;    						val+=(a[i][j]-'0')*(b[i+x][j+y]-'0');    					}    				}    				if(val>max) {    					max=val;    					X=x;    					Y=y;    				}    			}    		}    		out.println(X+" "+Y);    		    	}        out.flush();        out.close();    }}
