// practice with rainboyimport java.io.*;import java.util.*; public class CF229A extends PrintWriter {	CF229A() { super(System.out, true); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF229A o = new CF229A(); o.main(); o.flush();	} 	void main() {		int n = sc.nextInt();		int m = sc.nextInt();		int[] cc = new int[m];		int[] dd = new int[m];		int ans = n * m;		while (n-- > 0) {			byte[] aa = sc.next().getBytes();			Arrays.fill(dd, m);			for (int j = 0; j < m; j++)				if (aa[j] == '1') {					dd[j] = 0;					for (int d = 1, h; d < m && aa[h = (j + d) % m] == '0'; d++)						dd[h] = Math.min(dd[h], d);					for (int d = 1, h; d < m && aa[h = (j - d + m) % m] == '0'; d++)						dd[h] = Math.min(dd[h], d);				}			for (int j = 0; j < m; j++) {				if (dd[j] == m) {					println(-1);					return;				}				cc[j] += dd[j];			}		}		for (int j = 0; j < m; j++)			ans = Math.min(ans, cc[j]);		println(ans);	}}
