import java.util.Scanner; public class Main {	public static void main(String[] args) {		Scanner scan = new Scanner(System.in); 		int s = scan.nextInt();		int n = scan.nextInt(); 		int[][] array = new int[n][2]; 		for (int i = 0; i < n; i++) {			array[i][0] = scan.nextInt();			array[i][1] = scan.nextInt();		} 		for (int i = 0; i < n; i++) {			if (s > array[i][0]) {				s += array[i][1];				array[i] = array[n - 1];				i = -1;				n--;			}		}		if (n == 0)			System.out.println("YES");		else			System.out.println("NO");			} }
