// practice with rainboyimport java.io.*;import java.util.*; public class CF235B extends PrintWriter {	CF235B() { super(System.out, true); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF235B o = new CF235B(); o.main(); o.flush();	} 	void main() {		int n = sc.nextInt();		double[] pp = new double[n];		for (int i = 0; i < n; i++)			pp[i] = sc.nextDouble();		double p = 0, ans = 0;		for (int i = 0; i < n; i++)			ans += p = (p + 1) * pp[i];		ans *= 2;		for (int i = 0; i < n; i++)			ans -= pp[i];		println(ans);	}}
