//package Practice3.CF235; import java.util.Scanner; public class CF235B {    public static void main(String[] args) {        Scanner s = new Scanner(System.in);        int n = s.nextInt();        double result = 0;        double previous = 0;        double current = 0;        for(int i = 0; i < n; i++) {            current = s.nextDouble();            previous *= current;            result += 2 * previous + current;            previous += current;        }        System.out.println(result);    }}
