// practice with rainboyimport java.io.*;import java.util.*; public class CF24B extends PrintWriter {	CF24B() { super(System.out); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF24B o = new CF24B(); o.main(); o.flush();	} 	static final int M = 1000, N = 50;	int compare(int[] aa, int[] bb, int i) {		while (i < N) {			if (aa[i] != bb[i])				return aa[i] - bb[i];			i++;		}		return 0;	}	int[] pp = { 25, 18, 15, 12, 10, 8, 6, 4, 2, 1 };	void main() {		int t = sc.nextInt();		String[] ss = new String[M];		int[][] kk = new int[M][N];		int[] qq = new int[M];		int n = 0;		while (t-- > 0) {			int m = sc.nextInt();			for (int j = 0; j < m; j++) {				String s = sc.next();				int i;				for (i = 0; i < n; i++)					if (ss[i].equals(s))						break;				if (i == n)					ss[n++] = s;				kk[i][j]++;				if (j < 10)					qq[i] += pp[j];			}		}		int i_ = 0;		for (int i = 1; i < n; i++)			if (qq[i_] < qq[i] || qq[i_] == qq[i] && compare(kk[i_], kk[i], 0) < 0)				i_ = i;		println(ss[i_]);		i_ = 0;		for (int i = 1; i < n; i++)			if (kk[i_][0] < kk[i][0] || kk[i_][0] == kk[i][0] && (qq[i_] < qq[i] || qq[i_] == qq[i] && compare(kk[i_], kk[i], 1) < 0))				i_ = i;		println(ss[i_]);	}}
