import java.io.BufferedReader;//import java.io.BufferedWriter;import java.io.IOException;import java.io.InputStreamReader;import java.io.PrintWriter;//import java.io.FileReader;//import java.io.FileWriter;//import java.lang.StringBuilder;//import java.util.StringTokenizer;import java.lang.Comparable;import java.util.Arrays;import java.util.HashMap;import java.util.ArrayList;Â public class F1Champions {        static int[] score = {25, 18, 15, 12, 10, 8, 6, 4, 2, 1};        public static void main(String[] args) throws IOException {        //BufferedReader in = new BufferedReader(new FileReader("input.txt"));        //PrintWriter out = new PrintWriter(new BufferedWriter(new FileWriter("output.txt")));        BufferedReader in = new BufferedReader(new InputStreamReader(System.in));        PrintWriter out = new PrintWriter(System.out);        int n = Integer.parseInt(in.readLine());        HashMap<String, Result> map = new HashMap<>();        ArrayList<String> name = new ArrayList<String>();        for(int i = 0; i < n; i++) {            int n2 = Integer.parseInt(in.readLine());            for(int j = 0; j < n2; j++) {                String str = in.readLine();                Result r;                if(map.containsKey(str)) {                    r = map.get(str);                } else {                    r = new Result(str);                    name.add(str);                }                r.place(j);                map.put(str, r);            }        }        n = name.size();        Result[] resultat = new Result[n];        for(int i = n-1; i > -1; i--) {            String str = name.remove(i);            resultat[i] = new Result(map.get(str));        }        Arrays.sort(resultat);        out.println(resultat[0].name);        for(int i = 0; i < n; i++) {            resultat[i].changeSort();        }        Arrays.sort(resultat);        out.println(resultat[0].name);        out.close();    }        static class Result implements Comparable<Result> {        String name;        int[] place;        int pts;        int n;        boolean p;                public Result(String str) {            name = str;            pts = 0;            n = 50;            place = new int[n];            p = true;        }                public Result(Result r) {            name = r.name;            pts = r.pts;            n = 50;            place = new int[n];            for(int i = 0; i < n; i++) {                place[i] = r.place[i];            }            p = r.p;        }                public void changeSort() {            p = !p;        }                public void place(int i) {            place[i]++;            if(i < 10)                pts += score[i];        }                public int compareTo(Result r) {            if(p) {                if(pts != r.pts) {                    return r.pts - pts;                } else                    for(int i = 0; i < n; i++)                        if(place[i] != r.place[i])                            return r.place[i] - place[i];            } else {                if(place[0] != r.place[0]) {                    return r.place[0] - place[0];                } else if(pts != r.pts) {                    return r.pts - pts;                } else                    for(int i = 1; i < n; i++)                        if(place[i] != r.place[i])                            return r.place[i] - place[i];            }            return 0;        }    }}
