//package first;//提交代码时，此行要注释掉import java.math.BigDecimal;//小数import java.math.BigInteger;//整数 import java.util.*; public class Main{	public static Scanner sc =new Scanner(System.in);	static int M =201000;static int N =2010;	public static void main( String args []) {				int n,k,a[] =new int[N],s[] =new int[N],m[] =new int [N],sum = 0,ans= 0;		n = sc.nextInt();		k =sc.nextInt();		Arrays.fill(m, 0);		for(int i = 1;i <= n;i++)			a[i] =sc.nextInt();		for(int i = 1;i <= n;i++)			s[i] =sc.nextInt();		for(int i = 1;i<=n;i++)			m[i] =Math.max(m[i-1], s[i]);		for(int i = 1;i<=n;i++) {			sum +=s[i];			while(a[i] >sum) {				ans +=k;				sum +=m[i];			}			sum -=a[i];			ans +=a[i];		}		System.out.println(ans);	} }	 	 
