import java.util.*;public class Main{	public static void main(String[] args) {		Scanner sc=new Scanner(System.in);	    int T=sc.nextInt();	    int min=Integer.MAX_VALUE;	    int max=Integer.MIN_VALUE;	    int ar[][]=new int[2][T];	    int result=-1;	   for(int i=0;i<T;i++){	        int first=sc.nextInt();	        int second=sc.nextInt();	        min=Math.min(min,first);	        max=Math.max(max,second);	        ar[0][i]=first;	        ar[1][i]=second;	    }	    	    for(int i=0;i<T;i++){	        if(ar[0][i]==min && ar[1][i]==max){	            result=i+1; break;	        }	    }	    System.out.println(result);	}}
