//package com.company; import java.util.*;import java.lang.*;import java.io.*; public class Main {     public static void main(String[] args)throws Exception {        Scanner sx = new Scanner(System.in);        int n= sx.nextInt();        int ax=0,ay=0,bx=0,by=0;        while(n-->0){            int t = sx.nextInt();            int a = sx.nextInt();            int b = sx.nextInt();            if(t==1){                ax+=a;                ay+=b;            }else{                bx+=a;                by+=b;            }        }        if(ax>=ay){            System.out.println("LIVE");        }else{            System.out.println("DEAD");        }        if(bx>=by){            System.out.println("LIVE");        }else{            System.out.println("DEAD");        }    }}
