// https://codeforces.com/problemset/problem/245/B import java.util.*;import java.io.IOException;import java.io.BufferedReader;import java.io.InputStreamReader; public final class InternetAddress {    public static void main(String []args) throws IOException{         BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        String s = br.readLine();         int start = 0;        if (s.charAt(0) == 'h') {            System.out.print("http://");            System.out.print(s.charAt(4));            start = 5;        } else {            System.out.print("ftp://");            System.out.print(s.charAt(3));            start = 4;        }         int i;        for(i=start;i<s.length();i++) {            if (s.charAt(i) == 'r' && s.charAt(i + 1) == 'u') {                System.out.print(".ru");                break;            }            System.out.print(s.charAt(i));        }         i += 2;         if (i < s.length()) {            System.out.println("/" + s.substring(i));        }    }}
