import java.util.*;import java.io.*;public class MyClass {    public static void main(String args[]) throws IOException{        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));        int n = Integer.parseInt(br.readLine());        int count0left = 0;        int count1left = 0;        int count0right = 0;        int count1right = 0;        while(n-->0){            String s[] = br.readLine().split(" ");            int l = Integer.parseInt(s[0]);            int r = Integer.parseInt(s[1]);            if(l == 0){                count0left = count0left + 1;            }else if(l == 1){                count1left = count1left + 1;            }            if(r == 0){                count0right = count0right + 1;            }else if(r == 1){                count1right = count1right + 1;            }        }        int rs = Math.min(count0left,count1left)+Math.min(count0right,count1right);        System.out.println(rs);    }}
