import java.util.Scanner;public class Practice {	public static void main(String[] args)	{		Scanner sc = new Scanner(System.in);		int count = 0;		int no = sc.nextInt();		int lo=0, lc=0, ro= 0, rc= 0;		for(int i=0; i<no; i++){			int l = sc.nextInt();			int r = sc.nextInt();			if(l==0){				lc++;			}else{				lo++;			}						if(r==0){				rc++;			}else{				ro++;			}		}						int minc = Math.min(lc, lo);		int minr = Math.min(rc, ro);		System.out.println(minc + minr);	}}
