import java.util.*; public class Main{        public static void main(String[] args){                Scanner scn = new Scanner(System.in);        int n = scn.nextInt();        scn.nextLine();        int[] arrayt = new int[n];        for(int i=0; i<n ; i++){            arrayt[i] = scn.nextInt();        }        selectEvenness(arrayt);        scn.close();    }        public static void selectEvenness(int[] array){        int count_even=0, count_odd =0, num1 = 0,num2 = 0, index_odd = 0, index_even =0;        for(int i=0; i<array.length; i++){            if(array[i]%2==1)            {                count_odd++;                index_odd = i+1;            }            else if(array[i]%2==0)            {                count_even++;                index_even = i+1;            }            if(count_even==1 && count_odd>1)            {                System.out.println(index_even);                break;            }            else if(count_odd==1 && count_even>1)            {                System.out.println(index_odd);                break;            }        }    }        }
