import java.util.*;public class asd{    static Scanner s=new Scanner(System.in);            public static void main(String args[])    {        int t=1;        while(t-->0)        {            solve();        }    }    static void solve()    {        int n=s.nextInt();        String str=s.next();        if(n%2==0)        {            for(int i=0;i<str.length();i++)            {                if(i%2==0&&i!=0)                    System.out.print("-"+str.charAt(i));                else                    System.out.print(str.charAt(i));            }        }        else        {            if(n==3)            { System.out.print(str.substring(0,3));return;}            System.out.print(str.substring(0,3));            for(int i=3;i<str.length();i++)            {                if(i%2==1&&i!=0)                   System.out.print("-"+str.charAt(i));                else                    System.out.print(str.charAt(i));            }                    }    }}
