import java.util.*;public class Main{	public static void main(String[] args) {		Scanner sc=new Scanner(System.in);		int n=sc.nextInt();		long[] x=new long[n+1];				for(int i=1;i<=n;i++) {			x[i]=sc.nextLong()^x[i-1];		}		long max=0;				for(int i=1;i<=n;i++) {									max=Math.max(x[i], max);			for(int j=0;j<i;j++) {								max=Math.max(x[i]^x[j], max);							}		}		System.out.println(max);	}}
