//package Coding; import java.io.*;import java.nio.Buffer;import java.util.*; public class CodeForces {    public static void main(String[] args) throws FileNotFoundException {        //Scanner in = new Scanner(System.in);        Scanner in = new Scanner(new File("input.txt"));        PrintWriter out = new PrintWriter(new File("output.txt"));        int b = in.nextInt();        int B = b;        int g = in.nextInt();        int G = g;        String ans = "";         while (b != 0 || g != 0 ) {            if (b == 0) {                ans += "G";                g--;            } else if (g == 0) {                ans += "B";                b--;            } else {                if (B > G) {                    ans += "BG";                } else {                    ans += "GB";                }                 g--;                b--;            }        }        out.println(ans);        in.close();        out.close();     }  }          
