    import java.io.BufferedReader;    import java.io.FileInputStream;    import java.io.FileNotFoundException;    import java.io.FileOutputStream;    import java.io.IOException;    import java.io.InputStreamReader;    import java.io.PrintWriter;    import java.util.Scanner;    import java.util.Vector;              public class A {             /**         * @param args         * @throws IOException          * @throws NumberFormatException          */        public static void main(String[] args) throws NumberFormatException, IOException {            BufferedReader in=new BufferedReader(new InputStreamReader(System.in));            PrintWriter out=new PrintWriter(System.out);                        in=new BufferedReader(new InputStreamReader(new FileInputStream("input.txt")));            out=new PrintWriter(new FileOutputStream("output.txt"));                        int n=Integer.parseInt(in.readLine());            String[] inp=in.readLine().split(" ");            Vector<Vector<Integer>> mas=new Vector<Vector<Integer>>();            for(int i=0; i<5001; i++){                mas.add(new Vector<Integer>());            }            for(int i=0; i<n*2; i++){                mas.get(Integer.parseInt(inp[i])).add(i+1);            }            boolean check=true;            for(int i=1; i<5001; i++){                if(mas.get(i).size()%2==1){                    check=false;                    break;                }            }            if(!check){                out.println(-1);            }            else{                for(int i=1; i<5001; i++){                    for(int j=0; j<mas.get(i).size(); j+=2){                        out.println(mas.get(i).get(j)+" "+mas.get(i).get(j+1));                    }                }            }                        out.close();        }         }
