import java.util.*;public class Main{	public static void main(String[] args) {		Scanner sc=new Scanner(System.in);	    String arr[][]=new String[8][8];	    for(int i=0;i<8;i++){	        String str=sc.nextLine();	        arr[i]=str.split("");	    }	    boolean b=true;	    for(int i=0;i<8;i++){	        for(int j=1;j<8;j++){	            if(arr[i][j-1].equals(arr[i][j])) {	                b=false; break;	            }	        }	    }	    	    System.out.println((b)?"YES":"NO");	}}
