// practice with rainboyimport java.io.*;import java.util.*; public class CF261B extends PrintWriter {	CF261B() { super(System.out, true); }	Scanner sc = new Scanner(System.in);	public static void main(String[] $) {		CF261B o = new CF261B(); o.main(); o.flush();	} 	void main() {		int n = sc.nextInt();		int[] aa = new int[n];		for (int i = 0; i < n; i++)			aa[i] = sc.nextInt();		int p = sc.nextInt();		long[][] dp = new long[p + 1][n + 1];		dp[0][0] = 1;		for (int i = 0; i < n; i++) {			int a = aa[i];			for (int h = p - a; h >= 0; h--)				for (int k = 0; k < n; k++)					dp[h + a][k + 1] += dp[h][k];		}		long[][] ch = new long[n + 1][n + 1];		ch[0][0] = 1;		for (int i = 1; i <= n; i++) {			ch[i][0] = 1;			for (int j = 1; j <= i; j++)				ch[i][j] = ch[i - 1][j] + ch[i - 1][j - 1];		}		double ans = 0;		for (int k = 1; k <= n; k++) {			long x = 0;			for (int h = 0; h <= p; h++)				x += dp[h][k];			ans += (double) x / ch[n][k];		}		println(ans);	}}
