import java.util.*;public class Game{public static void main(String[] args){Scanner s=new Scanner(System.in);int n=s.nextInt();int[] arr1=new int[n];int[] arr2=new int[n];for(int i=0;i<n;i++){arr1[i]=s.nextInt();arr2[i]=s.nextInt();}int count=0;for(int i=0;i<n;i++){for(int j=0;j<n;j++){if(i!=j && arr1[i]==arr2[j]){count++;}}}System.out.println(count);}}
