import java.util.*;public class Game{    public static void main(String[] args){        Scanner sc=new Scanner(System.in);        int t=sc.nextInt();        int[] h=new int[t];        int[] a=new int[t];        int count=0;        for(int i=0;i<t;i++)        {            h[i]=sc.nextInt();            a[i]=sc.nextInt();        }        for(int i=0;i<t;i++)        {            for(int j=0;j<t;j++)            {                if(i!=j && h[i]==a[j])                   ++count;            }        }        System.out.println(count);    }}
