#include<iostream>#include<cstdio>#include<algorithm>#include<set>#include<map>#include<queue>#include<cmath>#include<string>#include<cstring>#include<ctime>#include<stack>#include<bitset>#include<vector>#define next nxt#define itn int#define y1 y3647#define LL long longusing namespace std;inline void read(int &x){	x=0;	int f=1;	char ch=getchar();	while((ch<'0'||ch>'9')&&ch!='-')	ch=getchar();	if(ch=='-')	{		f=-1;		ch=getchar();	}	while(ch>='0'&&ch<='9')	{		x=x*10+ch-'0';		ch=getchar();	}	x*=f;}int i,j,k,m,n,s,t;const int N=1e5+10;struct box{	int x,l;}a[N],b[N];bool cmp(box aa,box bb){	return aa.l<bb.l;}int main(){	//freopen(".in","r",stdin);	//freopen(".out","w",stdout);	//freopen(".ans","w",stdout);	read(n);	int maxn=0;	for(i=1;i<=n;i++)	{		read(b[i].l),read(b[i].x);		maxn=max(maxn,b[i].l);	}	sort(b+1,b+n+1,cmp);	b[0].l=-1;	s=0;	for(i=1;i<=n;i++)	{		if(b[i].l==b[i-1].l)		a[s].x+=b[i].x;		else		{			s++;			a[s].l=b[i].l;			a[s].x=b[i].x;		}	}	for(i=maxn+1;i<=maxn+32;i++)	{		int p=1;		for(j=1;j<=s;j++)		{			int now=1,cc=0;			while(a[j].x>now)			{				now*=4;				cc++;			}			if(cc+a[j].l>i)			{				p=0;//				break;			}		}		if(p)		break;	}	printf("%d",i);	return 0;}
