//https://codeforces.com/problemset/problem/275/A import java.io.*;import java.util.*;  public class Main{   public static void main(String[] args) {      MyScanner sc = new MyScanner();      out = new PrintWriter(new BufferedOutputStream(System.out));            // Start writing your solution here. -------------------------------------      int [][]game = {{1,1,1},{1,1,1},{1,1,1}};      for (int i = 0; i < 3; i++) {         for (int j = 0; j < 3; j++) {             changeCluster(game, i, j, sc.nextInt());         }       }      for (int i = 0; i < 3; i++) {         for (int j = 0; j < 3; j++) {             out.print(game[i][j]);         }          out.println();      }            // Stop writing your solution here. -------------------------------------      out.close();   }     public static void changeCluster(int[][]game, int x, int y, int times) {        if(times%2 != 0) {            game[x][y] = game[x][y] == 0 ? 1 : 0;            if (x-1 >= 0  && x-1 < 3) {                game[x-1][y] = game[x-1][y] == 0 ? 1 : 0;            }             if (x+1 >= 0  && x+1 < 3) {                game[x+1][y] = game[x+1][y] == 0 ? 1 : 0;            }             if (y-1 >= 0  && y-1 < 3) {                game[x][y-1] = game[x][y-1] == 0 ? 1 : 0;            }             if (y+1 >= 0  && y+1 < 3) {                game[x][y+1] = game[x][y+1] == 0 ? 1 : 0;            }         }    }         /*      int n      = sc.nextInt();        // read input as integer      long k     = sc.nextLong();       // read input as long      double d   = sc.nextDouble();     // read input as double      String str = sc.next();           // read input as String      String s   = sc.nextLine();       // read whole line as String       int result = 3*n;      out.println(result);                    // print via PrintWriter      */                       //-----------PrintWriter for faster output---------------------------------   public static PrintWriter out;         //-----------MyScanner class for faster input----------   public static class MyScanner {      BufferedReader br;      StringTokenizer st;       public MyScanner() {         br = new BufferedReader(new InputStreamReader(System.in));      }       String next() {          while (st == null || !st.hasMoreElements()) {              try {                  st = new StringTokenizer(br.readLine());              } catch (IOException e) {                  e.printStackTrace();              }          }          return st.nextToken();      }       int nextInt() {          return Integer.parseInt(next());      }       long nextLong() {          return Long.parseLong(next());      }       double nextDouble() {          return Double.parseDouble(next());      }       String nextLine(){          String str = "";	  try {	     str = br.readLine();	  } catch (IOException e) {	     e.printStackTrace();	  }	  return str;      }    }   //--------------------------------------------------------}
