//package codeforces; import java.util.Scanner; public class CapitalSmall {		public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int n = sc.nextInt();		int k = sc.nextInt();						int maxJoy = Integer.MIN_VALUE;		for(int i = 0; i < n; i++) {			int joy = Integer.MIN_VALUE;			int fi = sc.nextInt();			int ti = sc.nextInt();			if(ti <= k) {				joy = fi;			}			else {				joy = fi - (ti - k);			}						maxJoy = Math.max(maxJoy, joy);		}				sc.close();		System.out.println(maxJoy);	}}
