#include <iostream>#include <string>#include <algorithm> using namespace std; const int N = 1e5 + 5; int n,a[N],s[N]; int solve(){    int ret = 0 , top = 0;    for(int i=0;i<n;i++)    {        while(top && a[s[top]] < a[i])            --top;        if(top)            ret = max(ret,a[s[top]] ^ a[i]);        s[++top] = i;    }    return ret;} int main(){    scanf("%d",&n);    for(int i=0;i<n;i++)        scanf("%d",&a[i]);    int ans = solve();    reverse(a,a+n);    ans = max(ans,solve());    printf("%d\n",ans);    return 0;}
