import java.io.*;import java.util.*;import java.lang.*;public class Solution{    public static void main(String args[]){        Scanner sc=new Scanner(System.in);        int n=sc.nextInt();        int c=0;        while(n-->0){            String s=sc.next();            if(s.contains("+"))                c++;            else                c--;        }        System.out.println(c);    }}
