//Created by Aminul on 10/9/2020. import java.io.*;import java.util.*; import static java.lang.Math.*; public class F {    public static void main(String[] args) throws Exception {        Scanner in = new Scanner(System.in);        PrintWriter pw = new PrintWriter(System.out);        int cnt = 0;        int p = in.nextInt();        for (int x = 1; x < p; x++) {            boolean ok = true;            for (int e = 1; e <= p - 1 && ok; e++) {                long pow = bigMod(x, e, p);                pow = (pow - 1 + p) % p;                if(e < p - 1) ok &= (pow != 0);                else ok &= (pow == 0);            }            if (ok) cnt++;        }         pw.println(cnt);        pw.close();    }     static long bigMod(long a, long p, long m) { // returns (a^p) % m        long res = 1 % m, x = a % m;        while (p > 0) {            if ((p & 1) > 0) res = (res * x) % m;            x = (x * x) % m;            p >>= 1;        }        return res;    }     static void debug(Object... obj) {        System.err.println(Arrays.deepToString(obj));    }}					    	 		 	 				 					    	
