import java.util.*;public class Practice{	public static void main(String[] args) {		Scanner in = new Scanner(System.in);		int n = in.nextInt();		int s = in.nextInt()-1;		int f = in.nextInt()-1;		int at = s;		int[] p = new int[n];		for(int i = 0; i < n; i ++)			p[i] = in.nextInt()-1;		int ans = 0;		while(at!=f&&ans<=n+1)		{			at=p[at];			ans++;		}		System.out.println(ans>n?-1:ans);			}}
