import java.util.Scanner;import java.lang.Math; public class Account {			public static void main (String[] args) {		Scanner sc= new Scanner(System.in) ;		int n= sc.nextInt() ;		int s= sc.nextInt() ;		int t= sc.nextInt() ;		int[] p= new int[n+1] ;		for(int i=1 ;i<=n ;i++ ) {			p[i] = sc.nextInt() ;		}		if(s == t) {			System.out.println("0");			return ;		}		int count= 0 ,strt= s ;		while(s != t) {			s= p[s] ;			count++ ;			if(s == strt) {				System.out.println("-1");				return ;			}else if(s == t ) {				System.out.println(count);			}					} 			}		}
