import java.util.*;import java.math.*;public class IQTest{	public static void main(String[] args)		{			Scanner sc = new Scanner(System.in);			char[][] arr = new char[4][4];			for(int i = 0;i<4;i++)			{				arr[i] = sc.next().toCharArray();			}			boolean fl = false;			outer:for(int i = 0;i<3;i++)			{				for(int j = 0;j<3;j++)				{					int black = 0;					int white = 0;					if(arr[i][j]=='#')						black++;					else						white++;					if(arr[i][j+1]=='#')						black++;					else						white++;					if(arr[i+1][j]=='#')						black++;					else						white++;					if(arr[i+1][j+1]=='#')						black++;					else						white++;					if(black == 4||black == 3 || white == 3||white == 4)					{						System.out.println("YES");						fl = true;						break outer;					}				}			}			if(!fl)				System.out.println("NO");		}}
