import java.util.*;public class Main{ public static void main(String args[])    {        Scanner in = new Scanner(System.in);        int n = in.nextInt();        int a[] = new int[n];        int p[] = new int[n];        for(int i=0 ; i<n ; i++)        {            a[i]=in.nextInt();            p[i]=a[i]+in.nextInt();        }        int flag=0;        for(int i=0 ; i<n ; i++)        {            for(int j=0 ; j<n ; j++)            {                if(i!=j)                {                   if(a[i]==p[j]&&p[i]==a[j])                   flag=1;                }            }        }        if(flag==1)        System.out.println("YES");        else        System.out.println("NO");    }}
