import java.util.*;import java.io.*;public class Main{	public static void main(String[] args) {		Scanner sc = new Scanner(System.in);		int t = sc.nextInt();		HashMap<Long, Long> map = new HashMap<Long, Long>();		while(t-- > 0 ) {		   long x = sc.nextLong();      		   long d = sc.nextLong();		   map.put(x, d);		}		boolean flag = false;		for(Map.Entry<Long, Long> entry: map.entrySet()) {		    if(map.containsKey(entry.getKey()+entry.getValue())) {		        long d = map.get(entry.getKey()+entry.getValue());		        if(entry.getKey()+entry.getValue()+d == entry.getKey()) {		            flag = true;		            break;		        }		    }		}		if(flag) {		    System.out.println("YES");		} else {		    System.out.println("NO");		}		sc.close();	}}
