import java.io.*;import java.util.*;import java.util.HashSet;import java.util.Scanner;import java.util.Set;import java.util.ArrayList;import java.util.Collections;import java.util.HashMap;import java.util.Map;import java.text.DecimalFormat;import java.lang.Math;import java.util.Iterator; public class b98{	public static void main(String[] args){		Scanner sc = new Scanner(System.in);		double l = sc.nextDouble();		double d = sc.nextDouble();		double v = sc.nextDouble();		int g = sc.nextInt();		int r = sc.nextInt();		double d1 = d/v;		int[] t = new int[(int)l+5+g+r];		for(int i = 1; i <t.length; i++){		    if(i%(g+r)==0){		        t[i] = 0;		    }		    else if(i%(g+r) <= g){		        t[i] = 1;		    }		    else{		        t[i] = 0;		    }		}		int t1 = (int)d1+1;		if(t[t1]!=1){		    while(t[t1]!=1){    		    t1++;    		}		    d1 += t1-1-d1;		}		d1 += (l-d)/v;		System.out.println(d1);	}	}
