import java.util.*;//foramtthing code alt+shif+F//remove line ctr+E public class OPPJava {     public static void main(String[] args) {         Scanner sc = new Scanner(System.in);         int n = sc.nextInt();        int m, x, y;         int a[] = new int[1000000];        for (int i = 1; i <= n; i++) {             a[i] = sc.nextInt();        }        m = sc.nextInt();        while (m > 0) {            x = sc.nextInt();            y = sc.nextInt();            a[x - 1] += y - 1;            a[x + 1] += a[x] - y;            a[x] = 0;            m--;        }         for (int i = 1; i <= n; i++) {            System.out.println(a[i]);        }     } }
