//package albert.codeforces.shaassandbookshelf294b; import java.util.ArrayList;import java.util.Collections;import java.util.List;import java.util.Scanner; public class Solution {    public static void main(String[] args) {        Scanner scan = new Scanner(System.in);        int n = scan.nextInt();        List<Integer> ones = new ArrayList<>();        List<Integer> twos = new ArrayList<>();        for (int i = 0; i < n; i++) {            int t = scan.nextInt();            int w = scan.nextInt();            if (t == 1) {                ones.add(w);            } else {                twos.add(w);            }        }        Collections.sort(ones);        Collections.sort(twos);        int th = ones.size() + 2 * twos.size();        int min = ones.size() + 2 * twos.size();        for (int i = 0; i <= ones.size(); i++) {            for (int j = 0; j <= twos.size(); j++) {                int wSum1 = sum(ones, i);                int wSum2 = sum(twos, j);                 if (wSum1 + wSum2 <= th - i - 2 * j) {                     min = Math.min(min, th - i - 2 * j);                 }            }        }        System.out.println(min);    }     private static int sum(List<Integer> arr, int lim) {        int s = 0;        for (int i = 0; i < lim; i++) {            s += arr.get(i);        }        return s;    }}
